package com.syntax.class01;

public class PrimativeDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//storing and representing whole numeric values;
		byte box1 = 20;
		short box2 = 100; // least popular
		int box3 = 100000; // popular (mostly used)
		long box4 = 100454750000L;

		// storing representing decimal values;
		float variable1 = 1.99F;
		double variable2 = 99.99; // popular

		// storing single characters

		char container = 'a';
		char dollar = '$';

		// storing Yes or No values

		boolean hot = true;
		boolean tired = false;

		System.out.println(variable2);// printing value of the decimals
		System.out.println(box3);// printing text variable

		System.out.println(tired);
		// System.out.println(variable3); error - variable does not exist
	}

}
